mapper.select:
	select * from 
( 
	select /*+ index_desc(board_m board_m_pk) */ rownum rn, wbno, id, bbno, header, title, content, indate, viewcount, filename from board_m 
	)
	where wbno = #{wbno}

mapper.update:
update board_m set 
id =#{id}, bbno=#{bbno}, header=#{header}, title=#{title}, content=#{content}, indate=#{indate}, filename=#{filename} 
where wbno = #{wbno}
